import React from 'react'
import gql from 'graphql-tag'
import { Mutation } from 'react-apollo'

import REPOSITORY_FRAGMENT from '../fragments'
import Link from '../../Link'
import Button from '../../Button'

import '../style.css';

const STAR_REPOSITORY = gql`
  mutation($id: ID!) {
    addStar(input: { starrableId: $id }) {
      starrable {
        id
        viewerHasStarred
      }
    }
  }
`

const UNSTAR_REPOSITORY = gql`
  mutation($id: ID!) {
    removeStar(input: { starrableId: $id }) {
      starrable {
        id
        viewerHasStarred
      }
    }
  }
`

const WATCH_REPOSITORY = gql`
  mutation($id: ID!) {
    updateSubscription(input: { subscribableId: $id, state: SUBSCRIBED }) {
      subscribable {
        id
        viewerSubscription
      }
    }
  }
`

const UNWATCH_REPOSITORY = gql`
  mutation($id: ID!) {
    updateSubscription(input: { subscribableId: $id, state: UNSUBSCRIBED }) {
      subscribable {
        id
        viewerSubscription
      }
    }
  }
`

const updateAddStar = (
  client, 
  { data: { addStar: { starrable: { id } } } },
) => {
  const repository = client.readFragment({
    id: `Repository:${id}`,
    fragment: REPOSITORY_FRAGMENT,
  })
}

const RepositoryItem = ({
  id,
  name,
  url,
  descriptionHTML,
  primaryLanguage,
  owner,
  stargazers,
  watchers,
  viewerSubscription,
  viewerHasStarred,
}) => (
  <div>
    <div className="RepositoryItem-title">
      <h2>
        <Link href={url}>{name}</Link>
      </h2>

      <div className="RepositoryItem-title-action">
        {stargazers.totalCount} Stars
      </div>
    </div>

    <div>
      {!viewerHasStarred ? (
        <Mutation 
          mutation={STAR_REPOSITORY} 
          variables={{ id }}
          update={updateAddStar}
        >
          {(addStar, { data, loading, error }) => (
            <Button
              className={'RepositoryItem-title-action'}
              onClick={addStar}
            >
              {stargazers.totalCount} Star
            </Button>
          )}
        </Mutation>
      ) : ( 
        <Mutation mutation={UNSTAR_REPOSITORY} variables={{ id }}>
          {(removeStar, { data, loading, error }) => (
            <Button
              className={'RepositoryItem-title-action'}
              onClick={removeStar}
            >
              {stargazers.totalCount} Unstar
            </Button>
          )}
        </Mutation>
      )}
    </div>

    <div>
      {
        console.log(id, name, viewerSubscription)
      }
      {viewerSubscription === "UNSUBSCRIBED" ? ( 
        <Mutation mutation={WATCH_REPOSITORY} variables={{ id }}>
          {(updateSubscription, { data, loading, error }) => (
            <Button
              className={'RepositoryItem-title-action'}
              onClick={updateSubscription}
            >
              {watchers.totalCount} Watch
            </Button>
          )}
        </Mutation>
      ) : (
        <Mutation mutation={UNWATCH_REPOSITORY} variables={{ id }}>
          {(updateSubscription, { data, loading, error }) => (
            <Button
              className={'RepositoryItem-title-action'}
              onClick={updateSubscription}
            >
              {watchers.totalCount} Unwatch
            </Button>
          )}
        </Mutation>
      )}
    </div>

    <div className="RepositoryItem-description">
      <div
        className="RepositoryItem-description-info"
        dangerouslySetInnerHTML={{ __html: descriptionHTML }}
      />
      <div className="RepositoryItem-description-details">
        <div>
          {primaryLanguage && (
            <span>Language: {primaryLanguage.name}</span>
          )}
        </div>
        <div>
          {owner && (
            <span>
              Owner: <a href={owner.url}>{owner.login}</a>
            </span>
          )}
        </div>
      </div>
    </div>
  </div>
);

export default RepositoryItem
